import 'package:fabrication2/Home/components/body.dart';import 'package:fabrication2/main.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';import '../shoppingcard.widget.dart';import '../size_config.dart';class HomeScreen extends StatefulWidget {  static String routeName = "/home";  @override  _HomeScreenState createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  @override  Widget build(BuildContext context) {    SizeConfig().init(context);    return Scaffold(      appBar: AppBar(        actions: [          FlatButton(              minWidth: 2,              padding: EdgeInsets.zero,              onPressed: (){                Navigator.push(context, MaterialPageRoute(builder: (context)=>                    ShoppingCard()));              },              child: Icon(Icons.shopping_bag)),          FlatButton(            minWidth: 2,              padding: EdgeInsets.zero,              onPressed: ()async  {            await FirebaseAuth.instance.signOut();            Navigator.of(context, rootNavigator: true)                .pushReplacement(MaterialPageRoute              (builder: (context)=> MyApp()));          }, child:Icon(Icons.login_outlined)),        ],      ),      body: Body(),    );  }}