import 'package:fabrication2/Home/components/body.dart';import 'package:fabrication2/conf.dart';import 'package:fabrication2/main.dart';import 'package:firebase_auth/firebase_auth.dart';import 'package:flutter/material.dart';import 'package:google_fonts/google_fonts.dart';import '../shoppingcard.widget.dart';import '../size_config.dart';class HomeScreen extends StatefulWidget {  static String routeName = "/home";  @override  _HomeScreenState createState() => _HomeScreenState();}class _HomeScreenState extends State<HomeScreen> {  @override  Widget build(BuildContext context) {    SizeConfig().init(context);    return Scaffold(      drawer: Drawer(        child: Column(          children: [            Container(              height: 10,            ),            Container(              height: 120,              decoration: BoxDecoration(                color: Colors.orange              ),              child: Center(                child: Text("Espace logo"),              ),            ),            Card(child:            ListTile(              onTap: (){                Navigator.pop(context)  ;              },              leading:Icon(Icons.home_outlined,                color: Colors.orange,),              title: Text(                "الرئيسية",                textDirection: TextDirection.rtl,                style: GoogleFonts.tajawal(                    color: Colors.black                ),              ),            ),),            Card(child:            ListTile(              onTap: (){                Navigator.push(context, MaterialPageRoute(builder:                    (context)=>ShoppingCard()));              }              ,              leading:Icon(Icons.shopping_bag,color: Colors.orange,),              title: Text(                "سلة المشتريات",                textDirection: TextDirection.rtl,                style: GoogleFonts.tajawal(                    color: Colors.black                ),              ),            ),),            Card(child:              ListTile(                onTap: (){                  Navigator.push(context,MaterialPageRoute(builder: (context)=>                      ConfirmerOrder()));                },                leading:Icon(Icons.add_business,color: Colors.orange,),                title: Text(                  "مشترياتي",                  textDirection: TextDirection.rtl,                  style: GoogleFonts.tajawal(                    color: Colors.black                  ),                ),              ),),            Card(child:            ListTile(        onTap: (){},        leading:Icon(Icons.add_location_outlined      ,color: Colors.orange,),        title: Text(          " محلاتنا ",          textDirection: TextDirection.rtl,          style: GoogleFonts.tajawal(              color: Colors.black          ),        ),      ),),        Card(child: ListTile(          onTap: () async  {              await FirebaseAuth.instance.signOut();              Navigator.of(context, rootNavigator: true)                  .pushReplacement(MaterialPageRoute                (builder: (context)=> MyApp()));          },          leading:Icon(Icons.logout,            color: Colors.orange,),          title: Text(            "تسجيل الخروج",            textDirection: TextDirection.rtl,            style: GoogleFonts.tajawal(                color: Colors.black            ),          ),        )),          ],        ),      ),      appBar: AppBar(        elevation: 2,title: Text("الرئيسية ",style: GoogleFonts.tajawal(color: Colors.black),),        centerTitle: true,        actions: [          FlatButton(              minWidth: 2,              padding: EdgeInsets.zero,              onPressed: (){                Navigator.push(context, MaterialPageRoute(builder: (context)=>                    ShoppingCard(),                )                );              },              child: