import 'package:cloud_firestore/cloud_firestore.dart';import 'package:fabrication2/Model/Product.widget.dart';class prodcuct {  String name ;  String price ;  String pic  ;  String id ;  bool inhome ;  prodcuct({this.id,this.name,this.price,this.pic,this.inhome});  factory prodcuct.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data();    return prodcuct(        name: data["name"],        pic: data["pic"],        id: data["id"],        price: data["price"],      inhome : data["inhome"],    );  }map () {  return {    "name" : name ,    "price" :price ,    "pic" : pic ,    "id" : id,  };}}class Cat1 extends prodcuct{  String name ;  String price ;  String pic  ;  String id ;  bool inhome ;  Cat1({this.name,this.price,this.pic,this.id,this.inhome});  factory Cat1.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data();    return Cat1(      name: data["name"],      pic: data["pic"],      id: data["id"],      price: data["price"],      inhome : data["inhome"],    );  }}class Cat2 extends prodcuct{  String name ;  String price ;  String pic  ;  String id ;  bool inhome ;  Cat2({this.name,this.price,this.pic,this.id,this.inhome});  factory Cat2.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data();    return Cat2(        name: data["name"],        pic: data["pic"],        id: data["id"],        price: data["price"],      inhome : data["inhome"],    );  }}class Cat3 extends prodcuct{  String name ;  String price ;  String pic  ;  String id ;  bool inhome  ;  Cat3({this.name,this.price,this.pic,this.id,this.inhome});  factory Cat3.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data();    return Cat3(        name: data["name"],        pic: data["pic"],        id: data["id"],        price: data["price"],      inhome : data["inhome"],    );  }}class Cat4 extends prodcuct {  String name ;  String price ;  String pic  ;  String id ;  bool inhome  ;  Cat4({this.name,this.price,this.pic,this.id,this.inhome});  factory Cat4.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data();    return Cat4(        name: data["name"],        pic: data["pic"],        id: data["id"],        price: data["price"],      inhome : data["inhome"],    );  }}class DatabaseService {  final _db = FirebaseFirestore.instance;  Stream<List<Cat1>> stream_cats() {    var ref = _db.collection("cat1");    return ref.snapshots().map((event) =>        event.docs.map((e) => Cat1.fromFirestore(e))            .toList());  }  Stream<List<Cat2>> stream_cats2() {    var ref = _db.collection("cat2");    return ref.snapshots().map((event) =>        event.docs.map((e) => Cat2.fromFirestore(e))            .toList());  }  Stream<List<Cat3>> stream_cats3() {    var ref = _db.collection("cat3");    return ref.snapshots().map((event) =>        event.docs.map((e) => Cat3.fromFirestore(e))            .toList());  }  Stream<List<prodcuct>> streamuserproducts(String uid) {    var ref = _db.collection("ItemOrder").doc(uid).collection("items");    return ref.snapshots().map((event) =>        event.docs.map((e) => prodcuct.fromFirestore(e))            .toList());  }  Stream<List<Pending_Order>> streampendingOrder(String user) {    var ref =  _db.collection('PendingOrder').doc(user).collection('items').orderBy("id",descending: true);    return ref.snapshots().map((list) => list.docs.map((doc) =>        Pending_Order.fromFirestore(doc)).toList());  }}