import 'package:cloud_firestore/cloud_firestore.dart';class Product {  String Name ;  String Price ;  String Couleur ;  Product({this.Name,this.Price,this.Couleur});}class Pending_Order {  String activatedAdresse;  String codepromo;  String id;  String userid;  String phone;  String name;  String remise ;  bool livred ;  bool encours ;  Map<String, dynamic> order;  bool usecodepomo;  factory Pending_Order.fromFirestore(DocumentSnapshot doc) {    Map data = doc.data();    return Pending_Order(        id: doc.id,        encours: data['encours'],        name: data['name'],        livred: data['livred'],        remise : data['remise'],        activatedAdresse: data['activatedadresse'],        phone: data['phone'],        codepromo: data['code_de_promo'],        order: data['products'],        usecodepomo: data['usecodepromo'],        userid: data['userid']);  }  Pending_Order(      {this.activatedAdresse,        this.codepromo,        this.id,        this.userid,        this.encours,        this.phone,        this.livred,        this.order,        this.usecodepomo,        this.remise,        this.name});}class Order {  String name;  String qty;  String price;  String pic;  Order({this.name, this.qty, this.price, this.pic});}